mapper.select:
select * from (select ROWNUM rnum, a.*, b.login_date from (select * from MEMBER
where MEMBER_EMAIL like '%'||#{keyword}||'%' or MEMBER_NAME like '%'||#{keyword}||'%' or MEMBER_ADDRESS like '%'||#{keyword}||'%' or MEMBER_DATE like '%'||#{keyword}||'%'
) a, (select member_number, login_date from login_log) b where a.member_number = b.member_number(+) ) where rnum>#{startRow} and rnum<=#{endRow}
